mapper.select:
SELECT id, age, autograph, birthday, city, company, createTime, district, headImgUrl, hobby, hometown, nickName, personalityName, phone, province, region, sex, status, updateTime, userAccount, userPassword, userType, userName, country, description, university, work, IdCard as idCard, photo, approve, forbiden, sId, hotUser, tuiJian FROM s_user
and userName LIKE '%${keyword}%'
ORDER BY ${order} LIMIT #{pageNo} ,#{pageSize};

mapper.update:
update s_user
approve=#{approve}, isDelete=#{isDelete},
where id = #{id}
