mapper.select:
select id, name, create_time as createTime, update_time as updateTime, user_id as userId, is_delete as isDelete, status FROM s_vip
and name LIKE '%${keyword}%'
ORDER BY ${order} LIMIT #{pageNo} ,#{pageSize};

mapper.update:
update s_vip
status=#{status}, isDelete=#{isDelete},
where id = #{id}
